import React, { useState, useEffect } from "react";
import "./statetable.css";

const StateTable = (props) => {
  const [searchValue, setSearchValue] = useState("");

  //state search filter
  let filteredStates = props.stateData.filter((stateName) => {
    return stateName.state.toLowerCase().includes(searchValue.toLowerCase());
  });

  //   function compare(a, b) {
  //     const bandA = a.state.toUpperCase();
  //     const bandB = b.state.toUpperCase();

  //     let comparison = 0;
  //     if (bandA > bandB) {
  //       comparison = -1;
  //     } else if (bandA < bandB) {
  //       comparison = 1;
  //     }
  //     return comparison;
  //   }
  //   console.log(names.sort(compare))
  // };

  return (
    <div className="container">
      <div className="row before-table-row">
        <input
          className="form-control col-sm-3"
          id="tableSearch"
          type="text"
          placeholder="Search State"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
        />

        <div className="label ml-3">
          <div className="box rgba-red-slight text-danger">
            Today's High Infection
          </div>  
        </div>

        <div className="label ml-3">
          <div className="box rgba-green-slight text-success">
            Good Recoveries
          </div>  
        </div>

      </div>

      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th>State</th>
            <th className="confirmed"></th>
            <th className="active"></th>
            <th className="recovered"></th>
            <th className="deceased"></th>
          </tr>
        </thead>
        <tbody>
          {filteredStates.map((data, index) => (
            <tr key={index}>
              <td>{data.state}</td>
              <td
                className={`${
                  data.deltaconfirmed > 60 ? `rgba-red-slight` : ""
                }`}
              >
                {data.confirmed}
                {data.deltaconfirmed <= 0 ? (
                  ""
                ) : (
                  <div>
                    <i
                      className={`far fa-arrow-alt-circle-up cc_pointer ${props.textConfirmed}`}
                    >
                      {data.deltaconfirmed}
                    </i>
                  </div>
                )}
              </td>
              <td>
                {data.active}
                {data.deltaconfirmed <= 0 ? (
                  ""
                ) : (
                  <div>
                    <i
                      className={`far fa-arrow-alt-circle-up cc_pointer ${props.textActive}`}
                    >
                      {data.deltaconfirmed}
                    </i>
                  </div>
                )}
              </td>
              <td
                className={`${
                  data.deltarecovered > 20 ? `rgba-green-slight` : ""
                }`}
              >
                {data.recovered}
                {data.deltarecovered <= 0 ? (
                  ""
                ) : (
                  <div>
                    <i
                      className={`far fa-arrow-alt-circle-up cc_pointer ${props.textRecovered}`}
                    >
                      {data.deltarecovered}
                    </i>
                  </div>
                )}
              </td>
              <td>
                {data.deaths}
                {data.deltadeaths <= 0 ? (
                  ""
                ) : (
                  <div>
                    <i
                      className={`far fa-arrow-alt-circle-up cc_pointer ${props.textDeceased}`}
                    >
                      {data.deltadeaths}
                    </i>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StateTable;
