import React, { useState } from 'react';
import { connect } from 'react-redux';
import '../../index.css';

const LatestUpdates = (props) => {
	const [latestUpdates, setLatestUpdates] = useState(false);

	return (
		<div>
			<div>
				<p onClick={() => setLatestUpdates(!latestUpdates)} className="updates rgba-black-slight">
					Latest Updates
					{latestUpdates ? <i class="mt-1 far fa-bell-slash"></i> : <i class="mt-1 far fa-bell"></i>}
				</p>
			</div>

			{latestUpdates && (
				<div>
					{props.getLatestUpdates
						.reverse()
						.slice(0, 5)
						.map((item, index) => (
							<div>
								<p className="note note-secondary" key={index}>
									{item.update}
								</p>
								<p>sjkasjk</p>
							</div>
						))}
				</div>
			)}
		</div>
	);
};

const mapStateToProps = (state) => {
	return {
		getLatestUpdates: state.stats.latestUpdates,
	};
};

export default connect(mapStateToProps)(LatestUpdates);
