import React, { useState, useEffect } from "react";
import "./statetable.css";
import { ascending } from "d3";

const StateTable = (props) => {
  const [searchValue, setSearchValue] = useState("");

  //state search filter
  let filteredStates = props.stateData.filter((stateName) => {
    return stateName.state.toLowerCase().includes(searchValue.toLowerCase());
  });

  // // state sort
  // const stateSort = () => {
  //   let names = []
  //   names.push(props.stateData)
    // props.stateData.forEach(element => {
    //   names.push(element.state)
    // });
    // // console.log(names.sort())
    // console.log(names)
    // var newlen = names.sort(ascending)
    // console.log(newlen)
    
  //   function compare(a, b) {
  //     const bandA = a.state.toUpperCase();
  //     const bandB = b.state.toUpperCase();
    
  //     let comparison = 0;
  //     if (bandA > bandB) {
  //       comparison = -1;
  //     } else if (bandA < bandB) {
  //       comparison = 1;
  //     }
  //     return comparison;
  //   }
  //   console.log(names.sort(compare))
  // };


  return (
    <div className="mt-5">
      <input
        className="form-control mb-4 col-sm-3"
        id="tableSearch"
        type="text"
        placeholder="Search State"
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
      />

      <table className="table">
        <thead>
          <tr>
            <th>State</th>
            <th className="confirmed"></th>
            <th className="active"></th>
            <th className="recovered"></th>
            <th className="deceased"></th>
          </tr>
        </thead>
        <tbody>
          {filteredStates.map((data, index) => (
            <tr className="table" key={index}>
              <td> {data.state}</td>
              <td>
                {data.confirmed}
                <div>
                  <i
                    className={`far fa-arrow-alt-circle-up cc_pointer ${props.textConfirmed}`}
                  >
                    {data.deltaconfirmed}
                  </i>
                </div>
              </td>
              <td>
                {data.active}
                <div>
                  <i
                    className={`far fa-arrow-alt-circle-up cc_pointer ${props.textActive}`}
                  >
                    {data.deltaconfirmed}
                  </i>
                </div>
              </td>
              <td>
                {data.recovered}
                <div>
                  <i
                    className={`far fa-arrow-alt-circle-up cc_pointer ${props.textRecovered}`}
                  >
                    {data.deltarecovered}
                  </i>
                </div>
              </td>
              <td>
                {data.deaths}
                <div>
                  <i
                    className={`far fa-arrow-alt-circle-up cc_pointer ${props.textDeceased}`}
                  >
                    {data.deltadeaths}
                  </i>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StateTable;
