import React, { useState } from 'react';
import { connect } from 'react-redux';
import '../../index.css';

const LatestUpdates = (props) => {
	const [latestUpdates, setLatestUpdates] = useState(false);

	return (
		<div>
			<div>
				<p onClick={() => setLatestUpdates(!latestUpdates)} className="updates rgba-blue-slight">
					Latest Updates {latestUpdates ? <i class="far fa-bell"></i> : <i class="far fa-hand-point-down"></i>}
				</p>
			</div>

			{latestUpdates && (
				<div>
					{props.getLatestUpdates
						.reverse()
						.slice(0, 5)
						.map((item, index) => (
							<p className="note note-secondary" key={index}>
								{item.update}
							</p>
						))}
				</div>
			)}
		</div>
	);
};

const mapStateToProps = (state) => {
	return {
		getLatestUpdates: state.stats.latestUpdates,
	};
};

export default connect(mapStateToProps)(LatestUpdates);
