import React, { useState } from "react";
import "./statetable.css";

const StateTable = (props) => {
const [searchValue, setSearchValue] = useState("")

const handleSearch = (e) => {
  if(e.target.value !== ''){
    let searchResult = props.stateData.filter(data => {
      return data.state.toLowerState()
    })
  }
}

  return (
    <div className="mt-5">
      <input
        className="form-control mb-4 col-sm-3"
        id="tableSearch"
        type="text"
        placeholder="Search State"
        value={searchValue}
        onChange={handleSearch}
      />

      <table className="table">
        <thead>
          <tr>
            <th>State</th>
            <th className="confirmed"></th>
            <th className="active"></th>
            <th className="recovered"></th>
            <th className="deceased"></th>
          </tr>
        </thead>
        <tbody>
          {props.stateData.map((data, index) => (
            <tr className="table" key={index}>
              <td> {data.state}</td>
              <td >
                {data.confirmed}
                <div>
                  <i className={`far fa-arrow-alt-circle-up cc_pointer ${props.textConfirmed}`}>
                    {data.deltaconfirmed}
                  </i>
                </div>
              </td>
              <td>
                {data.active}
                <div>
                  <i className={`far fa-arrow-alt-circle-up cc_pointer ${props.textActive}`}>
                    {data.deltaconfirmed}
                  </i>
                </div>
              </td>
              <td>
                {data.recovered}
                <div>
                  <i className={`far fa-arrow-alt-circle-up cc_pointer ${props.textRecovered}`}>
                    {data.deltarecovered}
                  </i>
                </div>
              </td>
              <td>
                {data.deaths}
                <div>
                  <i className={`far fa-arrow-alt-circle-up cc_pointer ${props.textDeceased}`}>
                    {data.deltadeaths}
                  </i>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StateTable;
