import React, {useEffect, useState} from "react";
import './districtzone.css'

const DistrictZoneGraph = (props) => {
  const [redZone, setRedZone] = useState([])
  const [orangeZone, setOrangeZone] = useState([])
  const [greenZone, setGreenZone] = useState([])
  

const red = []
const orange = []
const green = []

useEffect(() => {

  props.zoneData.map(zone => {

  
    zone.zone === 'Red' && setRedZone(zone.district)
    zone.zone === 'Orange' && setOrangeZone(zone.district)
    zone.zone === 'Green' && setGreenZone(zone.district)

    setRedZone(red)
    setOrangeZone(orange)
    setGreenZone(green)
  })
},[green, orange, props, red])


  return (
    <div className="zones-data">
      <div className="row zone-col">
        <div className=" red lighten-4 zone">
          {redZone.map(dist => (
            <p>{dist}</p>
          ))}
        </div>
        <div className=" orange lighten-4 zone">
        {orangeZone.map(dist => (
            <p>{dist}</p>
          ))}
        </div>
        <div className=" green lighten-4 zone">
        {greenZone.map(dist => (
            <p>{dist}</p>
          ))}
        </div>
      </div>
    </div>
  )
}

export default DistrictZoneGraph
