import React from "react";
import "./statetable.css";

const StateTable = (props) => {
  var stateColor = [];
  props.stateData.map((data) => {
    if (data.confirmed <= 500) {
      stateColor.push("table-success");
    } else if (data.confirmed >= 500) {
      stateColor.push("table-warning");
    } else if (data.confirmed) {
      stateColor.push("table-warning");
    }
  });

  console.log(stateColor);

  return (
    <div className="mt-5">
      <input
        className="form-control mb-2 col-sm-3"
        id="tableSearch"
        type="text"
        placeholder="Search State"
      />

      <table className="table">
        <thead>
          <tr>
            <th>State</th>
            <th className="confirmed"></th>
            <th className="active"></th>
            <th className="recovered"></th>
            <th className="deceased"></th>
          </tr>
        </thead>
        <tbody>
          {props.stateData.map((data, index) => {
              <tr className="table" key={index}>
                <td className={stateColor}> {data.state}</td>
                <td>
                  {data.confirmed}
                  <div>
                    <i className="fas fa-arrow-up"> {data.deltaconfirmed} </i>
                  </div>
                </td>
                <td>{data.active}</td>
                <td>{data.recovered}</td>
                <td>{data.deaths}</td>
              </tr>;
          })}
        </tbody>
      </table>
    </div>
  );
};

export default StateTable;
