import React, { useState } from 'react';
import './demographics.css'

const PatientHeatMap = () => {
    const [isChecked, setIsChecked] = useState(false)

    const handleToggle = () => {
        setIsChecked(!isChecked)
    }

    return (
        <div className="container">

            <div className="row">
                <div className="custom-control custom-switch">
                    <input type="checkbox" className="custom-control-input sizer" id="customSwitch1" onClick={handleToggle} checked={isChecked} />
                    <label className="custom-control-label" htmlFor="customSwitch1">Expand </label>
                </div>
            </div>

            <div className="row mt-5">
                <div className="expanded-patient-box rgba-blue-slight">
                    {isChecked ? 
                    <p style={{
                        width : isChecked ? "50px" : "100px"
                    }}>P2545</p>
                    :
                    <div className="patient-box">

                    </div> 
                    }


                </div>
            </div>
        </div>
    )
}

export default PatientHeatMap
