import {
  GET_LIVE_STATS,
  ERROR_LIVE_STATS,
  FETCHING_LIVE_STATS,
} from "../actions/statActions";

const initialState = {
    fetchingStats: true,
    errorFetchingStats:'',
    liveStats: {}
}

const statsReducer = (state = initialState, action) => {
    switch(action.type) {
        case FETCHING_LIVE_STATS:
            return {
                ...state,
                fetchingStats: true
            }
    }
}
