// Darkmode Action
export const SET_MODE_DARK = "SET_MODE_DARK";
export const SET_MODE_LIGHT = "SET_MODE_LIGHT";


export const darkModeAction = (mode) => {
    return {
        type: SET_MODE_DARK,
        payload: mode
    }, {
        type: SET_MODE_LIGHT,
        payload: mode
    }
}
