import React, { useState } from 'react';
import { connect } from 'react-redux';
import '../../index.css';

const LatestUpdates = (props) => {
	// const [latestUpdates, setLatestUpdates] = useState([])

	const topUpdates = props.getLatestUpdates.reverse().slice(0, 5);

	return (
		<div>
			<div>
				<p className="updates rgba-blue-slight">Latest Updates</p>
			</div>
		</div>
	);
};

const mapStateToProps = (state) => {
	return {
		getLatestUpdates: state.stats.latestUpdates,
	};
};

export default connect(mapStateToProps)(LatestUpdates);
