import React, { useState } from 'react';
import {connect} from 'react-redux';

let factArr = []

const Factoids = (props) => {
    const [factoid, setFactoid] = useState(factArr)
    const [initialMsg, setInitialMsg] = useState("Stay Home Stay Happy")

    props.factoidsData.forEach(element => {
        factArr.push(element.banner)
    });

    factArr.forEach((item, index) => {
        setInterval(() => {
            setFactoid(item)
        }, 5000 * (index + 1));
    })

    return(
        <div className="d-flex justify-content-center mt-3">
            <p>{factoid}</p>
        </div>
    )
} 

const mapStateToProps = state => {
    return {
        factoidsData: state.stats.factoids
    }
}

export default connect(mapStateToProps)(Factoids)
