import React from "react";

const LiveStats = (props) => {
  return (
    <div>
      <div className="card mt-3 view overlay">
        <div className="card-body">
          <div className="container row">
            <h3
              className={`card-title ${props.badgeColor}`}
              style={{ fontSize: "20px" }}
            >
              {props.name}
            </h3>
            <h3
              className={`${props.textColor} ml-auto`}
              style={{ fontSize: "20px", fontWeight: 700 }}
            >
              {props.count}
            </h3>
          </div>
          <p className="card-text">
            Some quick example text to build on the panel title and make up the
            bulk of the panel's content Some quick example text to build on the
            panel title and make up the bulk of the panel's content.
          </p>

          <div className="container row">
            <h3
              className={`${props.textColor} card-title`}
              style={{ fontSize: "20px" }}
            >
              <i className="far fa-arrow-alt-circle-up cc_pointer"></i>
            </h3>
            <h3
              className={`${props.textColor} ml-auto`}
              style={{ fontSize: "20px", fontWeight: 700 }}
            >
              [+{props.todayCount}]
            </h3>
          </div>
        </div>
        <div
          className={`mask flex-center rgba-${props.hoverColor}-slight`}
        ></div>
      </div>
    </div>
  );
};

export default LiveStats;
