import React, { useState } from 'react';
import {connect} from 'react-redux';

const Factoids = (props) => {
    const [factoid, setFactoid] = useState([])

    props.factoidsData.forEach(element => {
        console.log(element.banner)
    });

    setInterval(() => {
        setFactoid(props.factoidsData)
    }, 1000);

    console.log(props.factoidsData)
    return(
        <div className="d-flex justify-content-center mt-3">
            <p></p>
        </div>
    )
} 

const mapStateToProps = state => {
    return {
        factoidsData: state.stats.factoids
    }
}

export default connect(mapStateToProps)(Factoids)
