import React, { useEffect } from "react";
import {connect} from 'react-redux';
import {getLiveStatsAction} from '../../store/actions/statActions';

const LiveStats = (props) => {
  useEffect(() => {
    props.getLiveStats()
  },[])

  return (
    <div>
      <div className="card mt-3 view overlay">
        <div className="card-body">
          <div className="container row">
            <h3
              className="badge badge-success card-title"
              style={{ fontSize: "20px" }}
            >
              Confirmed
            </h3>
            <h3
              className="text-success ml-auto"
              style={{ fontSize: "20px", fontWeight: 700 }}
            >
              18045
            </h3>
          </div>
          <p className="card-text">
            Some quick example text to build on the panel title and make up the
            bulk of the panel's content Some quick example text to build on the
            panel title and make up the bulk of the panel's content.
          </p>

          <div className="container row">
            <h3 className="card-title" style={{ fontSize: "20px" }}>
              Today
            </h3>
            <h3
              className="text-success ml-auto"
              style={{ fontSize: "20px", fontWeight: 700 }}
            >
              452
            </h3>
          </div>
        </div>
        <div className="mask flex-center rgba-blue-slight"></div>
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => {
  return {
    getLiveStats : () => dispatch(getLiveStatsAction())
  }
}

export default connect(null, mapDispatchToProps)(LiveStats);
