import { SET_MODE } from '../actions/darkModeActions';

const initialState = {
    mode: false
}

const darkModeReducer = (state = initialState, action) => {
    console.log(action)
    switch(action.type){
        case SET_MODE :
            return{
                mode : action.payload
            }
    }
}

export default darkModeReducer
