import React from "react";
import "./statetable.css";

const StateTable = (props) => {
  return (
    <div className="mt-5">
      <input
        className="form-control mb-2 col-sm-3"
        id="tableSearch"
        type="text"
        placeholder="Search State"
      />

      <table className="table">
        <thead>
          <tr>
            <th>State</th>
            <th className="confirmed"></th>
            <th className="active"></th>
            <th className="recovered"></th>
            <th className="deceased"></th>
          </tr>
        </thead>
        <tbody>
          {props.stateData.map((data, index) => (
            <tr className="table" key={index}>
              <td> {data.state}</td>
              <td className={props.textColor}>
                {data.confirmed}
                <div>
                  <i className="far fa-arrow-alt-circle-up cc_pointer">
                    {data.deltaconfirmed}
                  </i>
                </div>
              </td>
              <td>{data.active}
              <div>
                  <i className="far fa-arrow-alt-circle-up cc_pointer">
                    {data.deltaconfirmed}
                  </i>
                </div></td>
              <td>{data.recovered}
              <div>
                  <i className="far fa-arrow-alt-circle-up cc_pointer">
                    {data.deltarecovered}
                  </i>
                </div></td>
              <td>{data.deaths}
              <div>
                  <i className="far fa-arrow-alt-circle-up cc_pointer">
                    {data.deltadeaths}
                  </i>
                </div></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StateTable;
