import {
  GET_LIVE_STATS,
  ERROR_LIVE_STATS,
  FETCHING_LIVE_STATS,
} from "../actions/statActions";

const initialState = {
  fetchingStats: true,
  errorFetchingStats: "",
  liveStats: [],
  statesData: [],
};

const statsReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCHING_LIVE_STATS:
      return {
        ...state,
        fetchingStats: true,
      };
    case GET_LIVE_STATS:
      return {
        ...state,
        fetchingStats: false,
        errorFetchingStats: "",
        liveStats: action.payload,
      };
    case ERROR_LIVE_STATS:
      return {
        ...state,
        fetchingStats: false,
        errorFetchingStats: action.payload,
      };
    case GET_STATES_DATA : 
    return {
        ...state,
        statesData : action.payload
    }
    default:
      return state;
  }
};

export default statsReducer;
