import React, {useRef} from "react";
import * as d3 from "d3"

const Test = () => {
    const map = useRef(null)

    const svg = d3.select(map.current)
    svg.style("background-color", "red")

    return (
        <div>
            <svg id="chart" height="500" width="500" ref={map}>
            </svg>
        </div>
    )
}
export default Test
