import React, { useState } from "react";
import { connect } from "react-redux";
import * as Icon from "react-feather";
import { useEffectOnce, useLocalStorage } from "react-use";

const LatestUpdates = (props) => {
  //   console.log(props.applicationUpdate.lastupdatedtime);
  //   console.log(props.getLatestUpdates.toString());

  const [lastViewedLog, setLastViewedLog] = useLocalStorage(
    "lastViewedLog",
    null
  );
  const [newUpdate, setNewUpdate] = useLocalStorage("newUpdate", false);
  //   const [lastUpdated, setLastUpdated] = useState("");
  const [showUpdates, setShowUpdates] = useState(false);
  const [fetched, setFetched] = useState(false);

  //   setting localstorages
  useEffectOnce(() => {
    const lastTimestamp = props.getLatestUpdates.toString();
    setFetched(true);
    if (lastTimestamp !== lastViewedLog) {
      setNewUpdate(true);
      setLastViewedLog(lastTimestamp);
    }
  });

  //   setting last updated time for this wholeeeeee thing
  //   setLastUpdated(props.applicationUpdate.lastupdatedtime);

  return (
    <div className="container">

    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    getLatestUpdates: state.stats.latestUpdates,
  };
};

export default connect(mapStateToProps)(LatestUpdates);
