import React, { useEffect } from "react";
import NavBar from "./navbar/NavBar";
import { BrowserRouter, Route } from "react-router-dom";
import Home from './home/Home';
import Demographics from './demographics/Demographics';
import DeepDive from './deep-dive/DeepDive';
import '../index.css'
import { connect } from 'react-redux';
import { useLocalStorage } from 'react-use';



function App(props) {
  const [darkMode, setDarkMode] = useLocalStorage('dark-mode', false)

useEffect(() => {
  if(props.modeState === true){
    setDarkMode(true)
  }else if(props.modeState === false){
    setDarkMode(false)
  }
}, [props.modeState])

  return (
    <div className={`App ${props.modeState, darkMode && 'dark-body'}`} >
      <BrowserRouter>
        <NavBar />
        <Route exact path="/" component={Home} />
        <Route path="/demographics" component={Demographics} />
        <Route path="/deep-dive" component={DeepDive} />
      </BrowserRouter>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    modeState: state.darkMode.hasmode
  }
}

export default connect(mapStateToProps)(App);
