import React, { useState } from 'react';
import { connect } from 'react-redux';
import '../../index.css';

const LatestUpdates = (props) => {
	const [latestUpdates, setLatestUpdates] = useState(false);

	const topUpdates = props.getLatestUpdates.reverse().slice(0, 5);
  console.log(topUpdates)
	return (
		<div>
			<div >
				<p onClick={() => setLatestUpdates(!latestUpdates)} className="updates rgba-blue-slight">Latest Updates</p>
			</div>

			{latestUpdates && (
				<div>
          {topUpdates.map((item, index) => (
					  <p key={index}>{item.update}</p>
          ))}
				</div>
			)}
		</div>
	);
};

const mapStateToProps = (state) => {
	return {
		getLatestUpdates: state.stats.latestUpdates,
	};
};

export default connect(mapStateToProps)(LatestUpdates);
