import { GET_STATES_DATA } from "../actions/stateInfoActions";

const initialState = {
    statesData: []
}

const stateInfoReducer = (state = initialState, action) => {
    console.log(action)
    switch (action.type) {
        case GET_STATES_DATA:
            return {
                ...state,
                statesData: action.payload
            }
        default:
            return state
    }
}

export default stateInfoReducer
