import React, {useRef, useEffect} from "react";
import * as d3 from "d3";


const Learn = (props) => {
    const ref = useRef()

    useEffect(() => {
        const svgElement = d3.select(ref.current)
        svgElement.append("circle")
        .attr("cx", 150)
        .attr("cy", 70)
        .attr("r", 50)
    },[])

    return(
        <svg width={props.width} height={props.height} style={{border: "2px solid gold"}}>
            ref={ref}
        </svg>
    )
}

export default Learn
